"use strict";

var defaults = require("lodash.defaultsdeep");
var minify = require("html-minifier").minify;
var minimatch = require("minimatch");

module.exports = function htmlMinifier() {
	var pattern = arguments.length <= 0 || arguments[0] === undefined ? "*.html" : arguments[0];
	var options = arguments.length <= 1 || arguments[1] === undefined ? {} : arguments[1];

	defaults(options, {
		// See the README explainations of each of these options
		"collapseBooleanAttributes": true,
		"collapseWhitespace": true,
		"removeAttributeQuotes": true,
		"removeComments": true,
		"removeEmptyAttributes": true,
		"removeRedundantAttributes": true
	});
	return function plugin(files, metalsmith, done) {
		try {
			Object.keys(files).filter(minimatch.filter(pattern, {
				"matchBase": true
			})).forEach(function (file) {
				var data = files[file];
				var contents = data.contents.toString();
				var minified = minify(contents, options);
				data.contents = new Buffer(minified, "utf8");
			});
			done();
		} catch (e) {
			done(e);
		}
	};
};

